function doValidateCode(e, element, max_chars){

	max_chars--; //ne prende uno in più

    if (element.value.length > max_chars) {
        return false;
    }

	if (e.which){
		if ( e.which!=8 && e.which!=9 && (e.which<48 || e.which>57))
			return false;
    }
    else if(e.keyCode){
		if (e.keyCode!=8 && e.keyCode!=9 && (e.keyCode<48 || e.keyCode>57))
			return false;
    }
    
	return true;
}
