                    function setSortInfo(colIndex, order) {
                        colIndex = colIndex || 2;
                        $('#SortColumnIndex', mainPanel).val(colIndex);
       
                        var value = order && order === 'asc' ? 'true' : 'false' ;
                        $('#SortOrder', mainPanel).val(value);
