                                                    document.frmDownload.DTRiferimento.value = arrTokenDownload[0].dtRif;
                                                }
                                                document.frmDownload.TypeRep.value = arrTokenDownload[0].repository;
                                            }else{
                                                var tokArray = '';
                                                var spl = '#-#';
                                                $.each(arrTokenDownload , function(index, item) {
